mapper.insert:
INSERT INTO 
groups 
(
	group_leader
	,group_name
	,group_info

	,group_img

) 
VALUES 
( 
	#{memberNo}
	,#{groupName}
	,#{groupInfo}

	,#{groupImg}

)

    SELECT LAST_INSERT_ID()

mapper.select:
SELECT
,( SELECT member_name FROM member WHERE member.member_no = groups.group_leader ) AS groupLeader ,( SELECT COUNT(*) FROM mygroup WHERE mygroup_group = #{groupNo} ) AS groupSize
FROM groups WHERE 1!=1
OR group_no = #{groupNo}
OR group_name = #{groupName}
